import{_ as n,c as s,b as i,o as t}from"./app-Abdy_58m.js";const r={};function e(a,l){return t(),s("div",null,l[0]||(l[0]=[i('<h1 id="《兄弟情长》剧本" tabindex="-1"><a class="header-anchor" href="#《兄弟情长》剧本"><span>《兄弟情长》剧本</span></a></h1><h2 id="一、剧本信息" tabindex="-1"><a class="header-anchor" href="#一、剧本信息"><span>一、剧本信息</span></a></h2><ol><li><strong>剧本名称</strong> ：《兄弟情长》</li><li><strong>题材</strong> ：古风、情感、历史</li><li><strong>人数</strong> ：7 人（3 男 4 女，苏轼、苏辙、苏洵、苏轼妻妾王弗、王闰之、王朝云、苏辙挚友）</li><li><strong>时长</strong> ：5 - 6 小时</li></ol><h2 id="二、故事背景" tabindex="-1"><a class="header-anchor" href="#二、故事背景"><span>二、故事背景</span></a></h2><p>北宋时期，风云变幻。眉山苏氏一门，苏洵饱读诗书，志在仕途却坎坷不顺，将期望寄予苏轼与苏辙两兄弟身上。从眉山老家的朗朗书声，到进京赶考的崭露头角，再到步入仕途后，在波谲云诡的政治斗争中起起落落，三苏的命运与时代紧紧交织，而苏轼与苏辙之间的兄弟情，在这漫长岁月中，历经考验，愈发深厚。</p><h2 id="三、角色介绍" tabindex="-1"><a class="header-anchor" href="#三、角色介绍"><span>三、角色介绍</span></a></h2><ol><li><strong>苏轼</strong> ：性格豁达开朗，才华横溢，诗文冠绝当世。对生活充满热情，喜欢结交朋友，热爱游山玩水，却因心直口快，在官场上屡屡受挫。</li><li><strong>苏辙</strong> ：性格沉稳内敛，心思细腻，同样有着出众的才华。他对兄长苏轼极为敬重，在兄长遭遇困境时，总是竭尽全力相助。</li><li><strong>苏洵</strong> ：苏家大家长，饱读诗书，一心希望儿子们能在仕途上有所成就，为家族争光。他对儿子们要求严格，同时也言传身教，传授他们为人处世的道理和知识学问 。</li><li><strong>王弗</strong> ：苏轼的发妻，聪明伶俐，知书达理，与苏轼夫妻情深。她陪伴苏轼度过许多重要时光，在生活中给予苏轼细心的照料和支持，可惜早逝 。</li><li><strong>王闰之</strong> ：王弗的堂妹，在王弗去世后嫁给苏轼。她温柔善良，勤劳朴实，操持着家中大小事务，包容苏轼的随性，与苏轼相互扶持，共度风雨 。</li><li><strong>王朝云</strong> ：苏轼的侍妾，聪慧灵秀，能歌善舞，对苏轼一往情深。她理解苏轼的内心世界，与苏轼在精神上高度契合 。</li><li><strong>苏辙挚友</strong> ：与苏辙相识于微时，性格豪爽仗义，重情重义。他在苏辙的人生中扮演着重要角色，在苏辙遇到困难时，总是挺身而出，也见证了苏氏兄弟之间的情谊 。</li></ol><h2 id="四、剧本流程" tabindex="-1"><a class="header-anchor" href="#四、剧本流程"><span>四、剧本流程</span></a></h2><h3 id="第一幕-眉山岁月-回忆" tabindex="-1"><a class="header-anchor" href="#第一幕-眉山岁月-回忆"><span>第一幕：眉山岁月（回忆）</span></a></h3><ul><li><strong>时间</strong> ：宋仁宗嘉祐元年之前</li><li><strong>地点</strong> ：眉山苏家</li><li><strong>任务</strong> ：玩家们分别扮演各自角色，以童年和少年时期的视角开启故事。苏洵督促苏轼和苏辙读书学习，苏轼和苏辙在学堂上的趣事，以及他们与家中妻妾、苏辙挚友的相处点滴。玩家通过交流，了解彼此角色之间的关系和童年经历，初步建立情感联系。完成简单的学堂对诗、背书等小游戏，获胜者可获得小道具，用于后续环节。</li><li><strong>线索</strong> ：在书房中发现苏洵年轻时的文章手稿，暗示苏洵未竟的仕途梦想；找到苏轼和苏辙小时候互相交换的文房四宝，体现兄弟情谊。</li></ul><h3 id="第二幕-进京赶考" tabindex="-1"><a class="header-anchor" href="#第二幕-进京赶考"><span>第二幕：进京赶考</span></a></h3><ul><li><strong>时间</strong> ：宋仁宗嘉祐元年</li><li><strong>地点</strong> ：京城、赶考途中</li><li><strong>任务</strong> ：玩家分组完成保护行李、寻找丢失盘缠等任务。在科举考试环节，进行诗词创作比拼，由主持人根据诗词质量和符合情境程度打分，分数影响后续剧情发展。</li><li><strong>线索</strong> ：发现其他考生勾结考官的信件碎片；找到一封神秘人给苏轼的警告信，警告他不要太过张扬。</li></ul><h3 id="第三幕-初入仕途" tabindex="-1"><a class="header-anchor" href="#第三幕-初入仕途"><span>第三幕：初入仕途</span></a></h3><ul><li><strong>时间</strong> ：嘉祐二年后数年</li><li><strong>地点</strong> ：京城及各地任职地</li><li><strong>任务</strong> ：苏轼玩家需做出一些官场决策，如是否弹劾权贵、如何处理地方事务等，不同选择会导致不同结果；苏辙玩家要与其他官员周旋，完成拯救苏轼名声或争取资源的任务；妻妾玩家需处理家中琐事，维护家庭和睦，通过与其他角色互动获取线索。</li><li><strong>线索</strong> ：得到一封皇帝对苏轼态度转变的密信；发现苏辙在官场中对手的阴谋证据；找到苏轼妻妾与苏轼之间表达思念和担忧的信件。</li></ul><h3 id="第四幕-乌台诗案" tabindex="-1"><a class="header-anchor" href="#第四幕-乌台诗案"><span>第四幕：乌台诗案</span></a></h3><ul><li><strong>时间</strong> ：宋神宗元丰二年</li><li><strong>地点</strong> ：京城、监狱、黄州</li><li><strong>任务</strong> ：苏辙玩家需在有限时间内完成一系列营救任务，如拜访重要官员、寻找对苏轼有利的证据等；苏轼玩家在狱中通过回忆与其他角色的过往，完成情感沉浸环节，写下诀别信（可选择公开或保留）；妻妾玩家要面对来自外界的压力和质疑，坚守对苏轼的信任。</li><li><strong>线索</strong> ：找到苏轼诗文中被歪曲解读的关键证据；发现一些官员暗中帮助苏轼的线索；得到苏辙为救苏轼所付出努力的记录。</li></ul><h3 id="第五幕-黄州岁月" tabindex="-1"><a class="header-anchor" href="#第五幕-黄州岁月"><span>第五幕：黄州岁月</span></a></h3><ul><li><strong>时间</strong> ：元丰三年 - 元丰七年</li><li><strong>地点</strong> ：黄州</li><li><strong>任务</strong> ：苏轼玩家通过完成种地、与百姓交流等任务，获得百姓的信任和支持，改善生活状况；苏辙玩家通过信件与苏轼互动，解决苏轼在生活和精神上的困扰；妻妾玩家要照顾好苏轼的生活起居，同时参与苏轼的文化创作活动。</li><li><strong>线索</strong> ：发现苏轼在黄州创作的诗词手稿；找到苏辙寄给苏轼的家书，里面包含对朝政的分析和对兄长的关心；得到百姓对苏轼感恩戴德的信物。</li></ul><h3 id="第六幕-朝堂再起" tabindex="-1"><a class="header-anchor" href="#第六幕-朝堂再起"><span>第六幕：朝堂再起</span></a></h3><ul><li><strong>时间</strong> ：宋哲宗元祐年间</li><li><strong>地点</strong> ：京城</li><li><strong>任务</strong> ：苏轼和苏辙玩家在朝堂辩论环节，阐述自己的政治观点，争取其他官员的支持；其他玩家要通过与苏轼、苏辙互动，影响他们的决策，同时完成各自的隐藏任务。</li><li><strong>线索</strong> ：找到新旧两党斗争的关键证据；发现皇帝对两党态度的微妙变化线索；得到其他官员试图拉拢苏轼和苏辙的信件。</li></ul><h3 id="第七幕-晚年离别" tabindex="-1"><a class="header-anchor" href="#第七幕-晚年离别"><span>第七幕：晚年离别</span></a></h3><ul><li><strong>时间</strong> ：宋哲宗绍圣年间 - 宋徽宗建中靖国元年</li><li><strong>地点</strong> ：各地贬所、常州</li><li><strong>任务</strong> ：苏轼和苏辙玩家进行最后的情感交流，回顾一生经历，完成诀别场景；其他玩家表达对苏轼的怀念和对苏辙的安慰；苏辙玩家完成撰写苏轼墓志铭的任务。</li><li><strong>线索</strong> ：找到苏轼和苏辙年轻时约定的信物；发现苏轼生前最后的手稿，表达对苏辙的思念；得到苏辙为苏轼所作墓志铭的草稿。</li></ul><h2 id="五、结局" tabindex="-1"><a class="header-anchor" href="#五、结局"><span>五、结局</span></a></h2><ol><li><strong>完美结局</strong> ：苏轼和苏辙在政治斗争中虽然历经磨难，但始终坚守自己的原则和兄弟情谊。他们的政治理念在一定程度上得到认可，晚年生活相对安稳。苏轼和妻妾们相互陪伴，度过幸福时光；苏辙与挚友情谊深厚。最后，苏轼在平静中离世，苏辙为他举办隆重葬礼，兄弟二人的故事成为佳话流传后世。</li><li><strong>遗憾结局</strong> ：苏轼因政治迫害最终含冤而死，苏辙虽竭尽全力却未能挽回兄长性命。他在悲痛中为苏轼讨回公道，揭露政敌阴谋，但心中的伤痛永远无法抹去。苏轼妻妾们守着对苏轼的回忆度过余生，苏辙挚友一直陪伴在苏辙身边，安慰他走出阴影 。</li><li><strong>普通结局</strong> ：苏轼和苏辙在政治斗争中妥协，放弃部分理念，换取相对安稳的生活。他们依然保持着深厚的兄弟情，在闲暇时光相聚，回忆往昔。苏轼妻妾们和苏辙挚友见证了他们的一生，各自有着平凡而充实的生活 。</li></ol>',24)]))}const g=n(r,[["render",e]]),h=JSON.parse('{"path":"/docs/jubensha/1.html","title":"《兄弟情长》","lang":"en-US","frontmatter":{"title":"《兄弟情长》","date":"2020/05/29"},"headers":[{"level":2,"title":"一、剧本信息","slug":"一、剧本信息","link":"#一、剧本信息","children":[]},{"level":2,"title":"二、故事背景","slug":"二、故事背景","link":"#二、故事背景","children":[]},{"level":2,"title":"三、角色介绍","slug":"三、角色介绍","link":"#三、角色介绍","children":[]},{"level":2,"title":"四、剧本流程","slug":"四、剧本流程","link":"#四、剧本流程","children":[{"level":3,"title":"第一幕：眉山岁月（回忆）","slug":"第一幕-眉山岁月-回忆","link":"#第一幕-眉山岁月-回忆","children":[]},{"level":3,"title":"第二幕：进京赶考","slug":"第二幕-进京赶考","link":"#第二幕-进京赶考","children":[]},{"level":3,"title":"第三幕：初入仕途","slug":"第三幕-初入仕途","link":"#第三幕-初入仕途","children":[]},{"level":3,"title":"第四幕：乌台诗案","slug":"第四幕-乌台诗案","link":"#第四幕-乌台诗案","children":[]},{"level":3,"title":"第五幕：黄州岁月","slug":"第五幕-黄州岁月","link":"#第五幕-黄州岁月","children":[]},{"level":3,"title":"第六幕：朝堂再起","slug":"第六幕-朝堂再起","link":"#第六幕-朝堂再起","children":[]},{"level":3,"title":"第七幕：晚年离别","slug":"第七幕-晚年离别","link":"#第七幕-晚年离别","children":[]}]},{"level":2,"title":"五、结局","slug":"五、结局","link":"#五、结局","children":[]}],"git":{"createdTime":1751441613000,"updatedTime":1751441613000,"contributors":[{"name":"XokoukioX","email":"3282076201@qq.com","commits":1}]},"filePathRelative":"docs/jubensha/1.md"}');export{g as comp,h as data};
