import{_ as n,c as e,b as i,o as a}from"./app-Abdy_58m.js";const s={};function t(r,l){return a(),e("div",null,l[0]||(l[0]=[i('<h1 id="《三苏疑云》剧本" tabindex="-1"><a class="header-anchor" href="#《三苏疑云》剧本"><span>《三苏疑云》剧本</span></a></h1><h2 id="剧本信息" tabindex="-1"><a class="header-anchor" href="#剧本信息"><span>剧本信息</span></a></h2><ul><li><strong>剧本类型</strong> ：古风历史、悬疑推理、情感沉浸</li><li><strong>玩家人数</strong> ：6 人（3 男 3 女，可反串）</li><li><strong>游戏时长</strong> ：4 - 5 小时</li></ul><h2 id="故事背景" tabindex="-1"><a class="header-anchor" href="#故事背景"><span>故事背景</span></a></h2><p>北宋熙宁年间，王安石变法引发朝堂震动，新旧党争激烈交锋。文坛之上，眉山苏氏父子苏洵、苏轼、苏辙以文名远扬，苏洵新作《辨奸论》更是因影射时政，成为各方势力争夺的焦点。在一个暴雨倾盆的夜晚，苏洵书房突然燃起诡异大火，虽火势及时扑灭，但苏洵却葬身火海，未完成的书稿也在火中化为灰烬。现场残留的神秘火漆印、被烧毁的半封密信，以及书房内奇怪的打斗痕迹，都昭示着这场火灾绝非意外。是政敌为阻挠书稿问世痛下杀手？还是家族内部矛盾激化酿成悲剧？亦或是另有神秘势力暗中操控？玩家将扮演与苏家关系密切的关键人物，在迷雾重重的线索中追寻真相，揭开隐藏在墨香背后的惊天阴谋。</p><h2 id="角色介绍" tabindex="-1"><a class="header-anchor" href="#角色介绍"><span>角色介绍</span></a></h2><ol><li><strong>程夫人</strong> ：苏洵之妻，聪慧睿智、端庄大气。她出身名门，不仅将苏家上下打理得井井有条，还以过人的见识支持丈夫和儿子们的文学与仕途追求。苏洵离世后，她在悲痛之余，眼中闪过一丝不易察觉的警惕，似乎早已预感到这场灾难的来临。</li><li><strong>王朝云</strong> ：苏轼侍妾，才情出众、温柔体贴。自幼生活坎坷，被苏轼赏识纳为侍妾后，便一心追随。她对苏轼的文学创作极为关注，也因此卷入了这场风波，在众人面前，她小心翼翼地隐藏着自己知晓的某些秘密。</li><li><strong>柳如烟</strong> ：汴京第一茶楼 “醉墨轩” 老板娘，风情万种、八面玲珑。茶楼是文人墨客、达官显贵聚集之地，她借此掌握着众多朝堂秘闻和文坛消息。与苏家来往密切，此次苏洵出事，她的茶楼似乎也与事件有着千丝万缕的联系。</li><li><strong>司马光</strong> ：旧党领袖，学识渊博、刚正不阿。与王安石政见不合，因苏洵的《辨奸论》对其颇为赏识。听闻苏洵噩耗后前来吊唁，言语间对变法派颇多质疑，他的到来究竟是为了缅怀故人，还是另有目的？</li><li><strong>吕惠卿</strong> ：王安石变法的得力助手，精明强干、野心勃勃。因苏洵的文章暗含对变法的批判，对苏家心存不满。他在苏洵出事当晚曾在苏家附近出现，面对众人询问，他态度傲慢，极力撇清与事件的关系。</li><li><strong>陈安</strong> ：苏家护院统领，武艺高强、沉默寡言。跟随苏家多年，忠心耿耿，负责苏家的安全保卫。苏洵遇害当晚，他的巡逻路线出现异常，且身上有不明伤痕，他究竟在守护什么秘密？</li></ol><h2 id="剧本流程" tabindex="-1"><a class="header-anchor" href="#剧本流程"><span>剧本流程</span></a></h2><h3 id="第一幕-烈火惊变-30-分钟" tabindex="-1"><a class="header-anchor" href="#第一幕-烈火惊变-30-分钟"><span>第一幕：烈火惊变（30 分钟）</span></a></h3><ul><li><strong>开场</strong> ： <ul><li>暴雨夜后的清晨，玩家们以各自角色身份陆续来到苏家。苏宅内一片狼藉，苏洵的灵堂前烟雾缭绕，气氛压抑而紧张。众人相互寒暄，表面上是为苏洵之死悲痛，实则各自心怀鬼胎。每位玩家获得角色专属的初始任务和秘密，如隐藏与某党派的关系、掩盖自己在案发当晚的行动等。通过简单的自我介绍和交谈，初步构建角色间的关系网，同时留意他人的言辞和反应，捕捉可能的线索。</li><li><strong>任务</strong> ：玩家需在交谈中收集苏洵生前与各方的矛盾冲突、案发当晚的异常情况等基础线索，同时巧妙隐藏自身关键信息，防止过早暴露自己的秘密和动机。</li></ul></li></ul><h3 id="第二幕-火场探秘-40-分钟" tabindex="-1"><a class="header-anchor" href="#第二幕-火场探秘-40-分钟"><span>第二幕：火场探秘（40 分钟）</span></a></h3><ul><li><strong>事件</strong> ： <ul><li>众人决定共同探寻火灾真相，来到已成废墟的苏洵书房。尽管现场被大火破坏严重，但仍残留诸多可疑线索：带有特殊花纹的火漆印、烧毁的半封密信上隐约可见的字迹、书架下奇怪的脚印、以及散落在角落的神秘药瓶。</li><li><strong>线索</strong> ：玩家分组在书房及苏宅其他区域展开细致搜寻。过程中会发现更多关键线索，如程夫人房中的一封未寄出的家书，暗示她对某些势力的担忧；柳如烟茶楼的账本，记录着与神秘人的频繁交易；吕惠卿书房中的一份针对苏家的计划草稿（部分被损毁）。玩家之间可进行私聊，交换线索、互相试探，挖掘出更多隐藏在角色背后的故事和案件相关信息。</li></ul></li></ul><h3 id="第三幕-迷雾破局-60-分钟" tabindex="-1"><a class="header-anchor" href="#第三幕-迷雾破局-60-分钟"><span>第三幕：迷雾破局（60 分钟）</span></a></h3><ul><li><strong>讨论</strong> ： <ul><li>玩家们围坐在一起，整合所有线索，展开激烈的推理辩论。深入分析每个角色的作案动机、时间线，以及线索之间的逻辑关联。随着讨论的推进，会触发隐藏剧情，如王朝云与某神秘人的过往、司马光与苏洵的秘密约定、陈安不为人知的身世等，这些剧情将进一步揭示案件背后复杂的人物关系和利益纠葛。</li><li><strong>任务</strong> ：玩家需凭借推理能力找出导致苏洵死亡的真凶，还原整个作案过程，同时揭开书稿被毁背后的阴谋。通过投票指认凶手，投票结束后，主持人揭晓真相，复盘整个案件，讲述故事背后的爱恨情仇、党派纷争以及人性的抉择，让玩家沉浸在北宋风云变幻的历史浪潮之中。</li></ul></li></ul>',14)]))}const h=n(s,[["render",t]]),d=JSON.parse('{"path":"/docs/jubensha/2.html","title":"《三苏疑云》","lang":"en-US","frontmatter":{"title":"《三苏疑云》","date":"2020/05/29"},"headers":[{"level":2,"title":"剧本信息","slug":"剧本信息","link":"#剧本信息","children":[]},{"level":2,"title":"故事背景","slug":"故事背景","link":"#故事背景","children":[]},{"level":2,"title":"角色介绍","slug":"角色介绍","link":"#角色介绍","children":[]},{"level":2,"title":"剧本流程","slug":"剧本流程","link":"#剧本流程","children":[{"level":3,"title":"第一幕：烈火惊变（30 分钟）","slug":"第一幕-烈火惊变-30-分钟","link":"#第一幕-烈火惊变-30-分钟","children":[]},{"level":3,"title":"第二幕：火场探秘（40 分钟）","slug":"第二幕-火场探秘-40-分钟","link":"#第二幕-火场探秘-40-分钟","children":[]},{"level":3,"title":"第三幕：迷雾破局（60 分钟）","slug":"第三幕-迷雾破局-60-分钟","link":"#第三幕-迷雾破局-60-分钟","children":[]}]}],"git":{"createdTime":1751441613000,"updatedTime":1751441613000,"contributors":[{"name":"XokoukioX","email":"3282076201@qq.com","commits":1}]},"filePathRelative":"docs/jubensha/2.md"}');export{h as comp,d as data};
