import{_ as n,c as e,b as i,o as a}from"./app-Abdy_58m.js";const s={};function t(r,l){return a(),e("div",null,l[0]||(l[0]=[i('<h1 id="《墨香谜局》剧本" tabindex="-1"><a class="header-anchor" href="#《墨香谜局》剧本"><span>《墨香谜局》剧本</span></a></h1><h2 id="剧本信息" tabindex="-1"><a class="header-anchor" href="#剧本信息"><span>剧本信息</span></a></h2><ul><li><strong>剧本类型</strong> ：古风历史、悬疑推理、情感沉浸</li><li><strong>玩家人数</strong> ：6 人（3 男 3 女，可反串）</li><li><strong>游戏时长</strong> ：4 - 5 小时</li></ul><h2 id="故事背景" tabindex="-1"><a class="header-anchor" href="#故事背景"><span>故事背景</span></a></h2><p>北宋熙宁年间，朝堂新旧党争暗潮汹涌，文坛亦风云变幻。眉山苏氏父子苏洵、苏轼、苏辙以惊世才华名动汴京，引得各方势力觊觎。恰逢苏洵新作《辨奸论》即将付梓，其中锋芒直指朝堂隐秘。然而，就在书稿即将完成之际，苏洵却在深夜暴毙书房，珍贵书稿不翼而飞。究竟是政敌暗中使坏？还是家族内部暗藏矛盾？又或是江湖势力蓄意破坏？ 玩家将化身与苏家渊源颇深的关键人物，在蛛丝马迹中抽丝剥茧，探寻命案真相与书稿下落，感受北宋风云下的权谋与情义。</p><h2 id="角色介绍" tabindex="-1"><a class="header-anchor" href="#角色介绍"><span>角色介绍</span></a></h2><ul><li><strong>苏夫人</strong> ：苏洵之妻，苏轼、苏辙之母。温婉贤淑却不失坚韧，多年来操持家务，将苏家上下打理得井井有条。知晓苏家诸多隐秘，也深谙朝堂纷争对苏家的影响，在苏洵离世后，她眼中藏着忧虑与秘密 。</li><li><strong>王润之</strong> ：苏轼续弦，性格豁达大气，悉心照料苏轼及其子女。她默默陪伴苏轼经历人生起伏，看似只关心家庭琐事，实则对苏轼的事业极为上心，暗中关注着各方对苏家的动向。</li><li><strong>史云娘</strong> ：汴京书坊老板娘，精明能干，与苏家有书籍刊印往来。表面上唯利是图，实则心怀壮志，她的书坊似乎与朝堂势力有着千丝万缕的联系，此次苏洵书稿之事，她表现出异于寻常的关注。</li><li><strong>范纯仁</strong> ：范仲淹之子，朝堂新贵，与苏轼兄弟交好，主张革新却又力图平衡各方关系。他突然到访苏家，称是为吊唁苏洵，但其言行举止间，似乎隐藏着对书稿的别样目的。</li><li><strong>章惇</strong> ：苏轼昔日好友，后因政见不同渐生嫌隙，成为新党中坚力量。他为人狠辣果决，此次出现在苏家附近，被仆人撞见，面对质问，他言辞闪烁，其真实来意令人起疑。</li><li><strong>文徵</strong> ：苏家老仆，跟随苏洵多年，忠心耿耿，知晓苏家许多陈年旧事。苏洵遇害当晚，他的行动轨迹存在诸多疑点，面对众人询问，他总是欲言又止。</li></ul><h2 id="剧本流程" tabindex="-1"><a class="header-anchor" href="#剧本流程"><span>剧本流程</span></a></h2><h3 id="第一幕-惊变突生-30-分钟" tabindex="-1"><a class="header-anchor" href="#第一幕-惊变突生-30-分钟"><span>第一幕：惊变突生（30 分钟）</span></a></h3><ul><li><strong>开场</strong> ： <ul><li>玩家们以各自角色身份齐聚苏家，苏洵的灵堂内气氛凝重。众人互相寒暄试探，表面上是为吊唁苏洵，实则各怀心思。通过简单的自我介绍与交谈，初步建立角色关系，同时每个玩家会获得自己角色的初始任务与秘密，如隐藏与某些势力的关联等。</li><li><strong>任务</strong> ：玩家需在交谈中观察他人反应，收集关于苏洵生前异常举动、近期与哪些人往来密切等基础线索，同时隐藏自身关键信息，避免过早暴露。</li></ul></li></ul><h3 id="第二幕-密室寻踪-40-分钟" tabindex="-1"><a class="header-anchor" href="#第二幕-密室寻踪-40-分钟"><span>第二幕：密室寻踪（40 分钟）</span></a></h3><ul><li><strong>事件</strong> ： <ul><li>众人在讨论案情时，发现苏洵书房门窗紧闭，形成密室状态，且现场除了尸体，还留有奇怪的墨渍痕迹、半块破碎的玉佩等特殊线索。</li><li><strong>线索</strong> ：玩家们分组在书房及苏家其他区域搜寻，会找到如苏洵生前与朝中大臣的密信草稿（部分内容被涂抹）、书桌上神秘的符号刻痕、史云娘书坊的账本（其中关于苏洵书稿刊印的记录被篡改）等关键线索。玩家之间可进行私聊，交换线索或互相试探，挖掘更多不为人知的故事。</li></ul></li></ul><h3 id="第三幕-真相角逐-60-分钟" tabindex="-1"><a class="header-anchor" href="#第三幕-真相角逐-60-分钟"><span>第三幕：真相角逐（60 分钟）</span></a></h3><ul><li><strong>讨论</strong> ： <ul><li>玩家们围坐在一起，整合所有线索，展开激烈的推理辩论。分析每个角色的作案动机、时间线，以及线索之间的关联。在讨论过程中，会触发一些隐藏剧情，如章惇与苏轼过往矛盾的详细情节、史云娘背后支持者的相关信息等。</li><li><strong>任务</strong> ：玩家们需找出杀害苏洵的真凶，还原作案手法与过程，同时破解书稿失踪之谜。最终通过投票指认凶手，投票结束后，主持人揭晓真相，复盘整个案件，让玩家感受故事背后的爱恨情仇与时代风云。</li></ul></li></ul>',14)]))}const h=n(s,[["render",t]]),d=JSON.parse('{"path":"/docs/jubensha/4.html","title":"《墨香谜局》","lang":"en-US","frontmatter":{"title":"《墨香谜局》","date":"2020/05/29"},"headers":[{"level":2,"title":"剧本信息","slug":"剧本信息","link":"#剧本信息","children":[]},{"level":2,"title":"故事背景","slug":"故事背景","link":"#故事背景","children":[]},{"level":2,"title":"角色介绍","slug":"角色介绍","link":"#角色介绍","children":[]},{"level":2,"title":"剧本流程","slug":"剧本流程","link":"#剧本流程","children":[{"level":3,"title":"第一幕：惊变突生（30 分钟）","slug":"第一幕-惊变突生-30-分钟","link":"#第一幕-惊变突生-30-分钟","children":[]},{"level":3,"title":"第二幕：密室寻踪（40 分钟）","slug":"第二幕-密室寻踪-40-分钟","link":"#第二幕-密室寻踪-40-分钟","children":[]},{"level":3,"title":"第三幕：真相角逐（60 分钟）","slug":"第三幕-真相角逐-60-分钟","link":"#第三幕-真相角逐-60-分钟","children":[]}]}],"git":{"createdTime":1751441613000,"updatedTime":1751441613000,"contributors":[{"name":"XokoukioX","email":"3282076201@qq.com","commits":1}]},"filePathRelative":"docs/jubensha/4.md"}');export{h as comp,d as data};
